mapper.insert:
INSERT INTO ms_goods( pcode, sc_code, category, sitename, title, image1, image2, image3, image4, image5, image6, orgprice, price, dc_rate, url, regdate, updatedate, display, width, height ) VALUES ( #{pCode}, #{scCode}, #{category}, #{siteName}, #{title}, #{image1}, #{image2}, #{image3}, #{image4}, #{image5}, #{image6}, #{orgPrice}, #{price}, #{dcRate}, #{url}, NOW(), NOW(), #{display},
'0',
#{width},
'0'
#{height}
)

mapper.update:
UPDATE ms_goods SET category = #{category}, title = #{title}, sitename = #{siteName}, image1 = #{image1}, image2 = #{image2}, image3 = #{image3}, image4 = #{image4}, image5 = #{image5}, image6 = #{image6}, orgprice = #{orgPrice}, price = #{price}, dc_rate = #{dcRate}, url = #{url}, updatedate = NOW(), display = #{display},
width = '0',
width = #{width},
height = '0'
height = #{height}
WHERE gscd = #{gsCd} AND sc_code = #{scCode}

update.foreach:
UPDATE ms_goods SET category = #{item.category}, title = #{item.title}, sitename = #{item.siteName}, image1 = #{item.image1}, image2 = #{item.image2}, image3 = #{item.image3}, image4 = #{item.image4}, image5 = #{item.image5}, image6 = #{item.image6}, orgprice = #{item.orgPrice}, price = #{item.price}, dc_rate = #{item.dcRate}, url = #{item.url}, updatedate = NOW(), display = #{item.display},
width = '0',
width = #{item.width},
height = '0'
height = #{item.height}
WHERE gscd = #{item.gsCd} AND sc_code = #{item.scCode}

mapper.select:
      SELECT gsCd, category FROM ms_goods
		WHERE sc_code = #{scCode}
	  AND pcode = #{pCode}

mapper.update:
UPDATE ms_goods SET display ='0'
  WHERE sc_code = #{scCode}

AND updatedate <= DATE_FORMAT(DATE_SUB(NOW(), INTERVAL #{soldOutPeriod} DAY), '%Y-%m-%d')
